from typing import Dict, List

def assess_investment_risk(assets: List[Dict[str, str]]):
    total_value = sum(float(asset['value']) for asset in assets)
    risk_score = 0
    for asset in assets:
        if asset['type'] == 'cryptocurrency':
            risk_score += 2  # Higher weight for crypto due to volatility
        elif asset['type'] == 'stock':
            risk_score += 1
        elif asset['type'] == 'bond':
            risk_score += 0.5
    risk_score /= len(assets)
    return risk_score

# Example usage
assets = [
    {'type': 'cryptocurrency', 'value': '10000'},
    {'type': 'stock', 'value': '5000'},
    {'type': 'bond', 'value': '5000'}
]
print(assess_investment_risk(assets))
